import React, { useState } from "react";
import Filter from "./components/Filter";
import PersonForm from "./components/PersonForm";
import Content from "./components/Content";

const App = () => {
  const [persons, setPersons] = useState([
    { name: "Arto Hellas", number: "040-123456" },
    { name: "Ada Lovelace", number: "39-44-5323523" },
    { name: "Dan Abramov", number: "12-43-234345" },
    { name: "Mary Poppendieck", number: "39-23-6423122" },
  ]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [newFilter, setNewFilter] = useState("");
  const [filteredPersons, setFilteredPersons] = useState("");

  const addPerson = (event) => {
    event.preventDefault();
    const newPerson = {
      name: newName,
      number: newNumber,
    };
    if (
      persons.some(
        (person) => person.name.toUpperCase() === newName.toUpperCase()
      )
    ) {
      alert(`${newName} is already added to phonebook`);
    } else {
      setPersons([...persons, newPerson]);
      setNewName("");
      setNewNumber("");
    }
  };

  // const handleFilter = (event) => {
  //   setNewFilter(event.target.value);
  //   let regex = new RegExp(newFilter, "ig");
  //   const result = persons.filter((person) =>
  //     person.name.match(regex)
  //   );
  //   setFilteredPersons(result);
  // };

  const handleFilter = (event) => {
    setNewFilter(event.target.value);
  };

  React.useEffect(() => {
    const regex = new RegExp(newFilter, "i");
    const result = persons.filter((person) => person.name.match(regex));
    setFilteredPersons(result);
  }, [newFilter]);

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter value={newFilter} onChange={handleFilter} />
      <h2>add a new</h2>
      <PersonForm
        onSubmit={addPerson}
        newName={newName}
        handleName={(event) => setNewName(event.target.value)}
        newNumber={newNumber}
        handleNumber={(event) => setNewNumber(event.target.value)}
      />
      <h2>Numbers</h2>
      <Content persons={persons} filteredPersons={filteredPersons} />
    </div>
  );
};

export default App;
