import React, { useState, useEffect } from "react";
import Filter from "./components/Filter";
import PersonForm from "./components/PersonForm";
import Content from "./components/Content";
import axios from "axios";

const App = () => {
  const [persons, setPersons] = useState([]);
  const [newName, setNewName] = useState("");
  const [newNumber, setNewNumber] = useState("");
  const [newFilter, setNewFilter] = useState("");
  const [filteredPersons, setFilteredPersons] = useState("");

  useEffect(() => {
    axios
      .get("http://0.0.0.0:3001/persons")
      .then((response) => setPersons(response.data));
  }, []);

  const addPerson = (event) => {
    event.preventDefault();
    const newPerson = {
      name: newName,
      number: newNumber,
    };
    if (
      persons.some(
        (person) => person.name.toUpperCase() === newName.toUpperCase()
      )
    ) {
      alert(`${newName} is already added to phonebook`);
    } else {
      setPersons([...persons, newPerson]);
      setNewName("");
      setNewNumber("");
    }
  };

  const handleFilter = (event) => {
    setNewFilter(event.target.value);
  };

  useEffect(() => {
    const regex = new RegExp(newFilter, "i");
    const results = persons.filter((person) => person.name.match(regex));
    setFilteredPersons(results);
  }, [newFilter, persons]);

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter value={newFilter} onChange={handleFilter} />
      <h2>add a new</h2>
      <PersonForm
        onSubmit={addPerson}
        newName={newName}
        handleName={(event) => setNewName(event.target.value)}
        newNumber={newNumber}
        handleNumber={(event) => setNewNumber(event.target.value)}
      />
      <h2>Numbers</h2>
      <Content persons={persons} filteredPersons={filteredPersons} />
    </div>
  );
};

export default App;
